for(var buttonWriteUs=document.querySelector(".btn-write-us"),popUp=document.querySelector(".modal-write-us"),btnClose=document.querySelector(".modal-close"),form=popUp.querySelector("form"),username=popUp.querySelector("[name=username]"),email=popUp.querySelector("[name=email]"),allNojs=document.querySelectorAll(".no-js"),i=0;i<allNojs.length;i++)allNojs[i].classList.remove("no-js");buttonWriteUs.addEventListener("click",function(e){e.preventDefault(),popUp.classList.add("modal-show")}),btnClose.addEventListener("click",function(e){e.preventDefault(),popUp.classList.remove("modal-show"),popUp.classList.remove("modal-error")}),form.addEventListener("submit",function(e){username.value&&email.value||(e.preventDefault(),popUp.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popUp.classList.contains("modal-show")&&(popUp.classList.remove("modal-show"),popUp.classList.remove("modal-error"))});
